mapper.select:
SELECT COUNT(*) AS number,FROM_UNIXTIME(a.`create_time`,'%Y-%m-%d') dates FROM zp_users a
where 1=1

    and FROM_UNIXTIME(a.`create_time`,'%Y-%m-%d')  >=  #{startDate}


    and FROM_UNIXTIME(a.`create_time`,'%Y-%m-%d')  <=  #{endDate}

GROUP BY dates

SELECT a.id as userId, a.mobile as mobile, a.`nickname` as nickname, a.`first_leader` as firstLeader, CASE WHEN a.level = 1 THEN '粉丝' WHEN a.level = 2 THEN '正式用户' WHEN a.level = 3 THEN '初级合伙人' WHEN a.level = 4 THEN '中级合伙人' WHEN a.level = 5 THEN '高级合伙人' WHEN a.level = 4 THEN '联合合伙人' END AS LEVEL, (SELECT COUNT(*) FROM zp_users WHERE first_leader = a.id) AS counts FROM zp_users a WHERE 1=1
AND a.first_leader = #{userId}
AND a.id IN (SELECT first_leader FROM zp_users)

select * from game.zp_users 
where 1 = 1

	AND mobile = #{mobile}


	AND id = #{id}


	AND reg_time >= #{regTimeStart}



	AND reg_time <= #{regTimeEnd}

 order by (select sum(price) from game.zp_user_exclusive_pig where user_id = game.zp_users.id and is_pig_lock = 0) desc, id asc
